javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
/* get build parameter */
hudson.model.Build build = hudson.model.Executor.currentExecutor().currentExecutable
hudson.model.ParametersAction parametersAction = build.getAction(hudson.model.ParametersAction)
hudson.model.ParameterValue parameterValue = parametersAction.getParameter('NAME')
String NAME = parameterValue.getValue()

/* define variables */
String ORGANIZATION = 'osu-mist'

/* create continuous integration job */
job(NAME + ' continuous integration') {
  jdk('Java 7')
  scm {
    github(ORGANIZATION+'/'+NAME, '**')
  }
  triggers {
    githubPush()
  }
  steps {
    gradle {
      useWrapper()
      tasks('clean')
      tasks('build')
    }
  }
  publishers {
    githubCommitNotifier()
  }
}
